/* FullCalendar JS */
document.addEventListener('DOMContentLoaded', function () {
    var calendarEl = document.getElementById('calendar');

    var calendar = new FullCalendar.Calendar(calendarEl, {
        initialView: 'dayGridMonth',
        events: [
            {
                title: 'Event 1',
                start: '2024-06-01'
            },
            {
                title: 'Event 2',
                start: '2024-06-05',
                end: '2024-06-07'
            }
        ]
    });

    calendar.render();
});
